<script>
export default {
    data() {
        return {
            aa: "获取验证码",
            bb: "",
            dab: "ture",
            cc: "",
            dd: 0,
            ee: "",
            ff: 1234,
            name: "my"
        };
    },
    methods: {
        test(val) {
            const a = Number(val);
            if (/^1[3456789]\d{9}$/.test(a)) {
                this.dab = false;
            } else {
                this.dab = true;
            }
        },
        text1() {
            let time = 30;
            let timer = setInterval(() => {
                this.dab = true;
                this.aa = "请稍等" + time;
                time--;
                if (time == 0) {
                    this.dab = false;
                    this.aa = "重新获取";
                    clearInterval(timer);
                }
            }, 1000);
        },
        text2() {
            if (this.bb == "") {
                this.cc = "请填写手机号";
                this.dd++;
                setTimeout(() => {
                    this.dd--;
                }, 2000);
            } else if (this.bb != "" && !/^1[3456789]\d{9}$/.test(this.bb)) {
                this.cc = "请填写合法的手机号";
                this.dd++;
                setTimeout(() => {
                    this.dd--;
                }, 2000);
            } else if (
                this.bb != "" &&
                /^1[3456789]\d{9}$/.test(this.bb) &&
                this.ee == ""
            ) {
                this.cc = "请输入验证码";
                this.dd++;
                setTimeout(() => {
                    this.dd--;
                }, 2000);
            } else if (
                this.bb != "" &&
                /^1[3456789]\d{9}$/.test(this.bb) &&
                this.ee != "1234"
            ) {
                this.cc = "验证码错误";
                this.dd++;
                setTimeout(() => {
                    this.dd--;
                }, 2000);
            } else if (
                this.bb != "" &&
                /^1[3456789]\d{9}$/.test(this.bb) &&
                this.ee == "1234"
            ) {
                localStorage.setItem("username", this.bb);
                this.$router.push({
                    name: this.name
                });
            }
        }
    }
};
